    state = {selectectedGuest: null};

    onWordEntered = (word) => {
        console.log(word);
    }

    componentDidMount() {
        console.log(Guests[1]);
        //this.setState({selectectedGuest: Guests[1]});
    }

    render() {
        return (
            <div>
                <div>
                    <SearchBar onWordEntered={this.onWordEntered} />
                </div>
                <div className="ui segment">
                    <TableList guests={Guests} tables={Tables}/>
                </div>
                <div className="ui segment">

                </div>
            </div>
        );
    }
}

export default App;
