import React from 'react';
import './app.css';
import SearchBar from './SearchBar';
import TableList from './TableList';
import GuestView from './GuestView';
import Guests from '../data/guest_list.json'
import Tables from '../data/tables.json'

class App extends React.Component {

    state = {selectectedGuest: null};

    onWordEntered = (word) => {
        console.log(word);
    }

    componentWillMount() {
        this.setState({selectectedGuest: Guests[1]});
    }

    onGuestSelected(guest) {
        this.setState({selectectedGuest: guest});  
    }

    render() {
        return (
            <div>
                <div>
                    <SearchBar onWordEntered={this.onWordEntered} />
                </div>
                <div className="ui segment">
                    <TableList guests={Guests} tables={Tables} onGuestSelected={this.onGuestSelected}/>
                </div>
                <div className="ui segment">
                    <GuestView guest={this.state.selectectedGuest}/>
                </div>
            </div>
        );
    }
}

export default App;
