import React from 'react';
import GuestItem from './GuestItem';

class TableItems extends React.Component {

    render() {

        const printGuests = this.props.guests.map( guest => {
            if(guest.Table.Name === this.props.table) {
                return <GuestItem key={guest.CODE} name={guest.Names} />;
            }
            return "";
        });

        return (
            <div className="ui blue cards tables" style={{margin:"0px 10px"}}>
                <div className="card">
                    <div className="content">  
                        <div className="center aligned header" style={{marginTop:"5px"}}>{this.props.table}</div> 
                        <div className="center aligned meta">10 Places disponibles</div>
                        <br/>
                        <div className="description" style={{marginBottom:"10px"}}>
                            <div className="ui celled ordered list">
                                {printGuests}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default TableItems;
