import React from 'react';
import './app.css';
import SearchBar from './SearchBar';
import TableList from './TableList';
import GuestView from './GuestView';
//import Guests from '../data/guest_list.json'
//import Tables from '../data/tables.json'
import axios from 'axios'
import { resolve } from 'path';

class App extends React.Component {

    state = {guestsToDisplay: [], myGuests: [], selectectedGuest: null, tablesToDisplay: [], tables: []};

    onWordEntered = (word) => {

        const wordLower = word.toLowerCase();
        this.setState({guestsToDisplay: [], tablesToDisplay: []});
        if(word === "") {
            this.setState({guestsToDisplay: this.state.myGuests, tablesToDisplay: Tables});
        } else {
            this.setState(state => {
                const guestsToDisplay = state.myGuests.map(g => {
                    if(g.CODE.toLowerCase().indexOf(wordLower) > -1) {
                        return g;
                    }
                    if(g.Names.toLowerCase().indexOf(wordLower) > -1) {
                        return g;
                    }
                    if(g.Table.toLowerCase().indexOf(wordLower) > -1) {
                        return g;
                    }
                    return {};
                });

                const tablesToDisplay = Tables.map( table => {
                    for(let guest of guestsToDisplay) {
                        if(guest.Table === table.Name) {
                            return table;
                        }
                    }
                    return null;
                });
                return {
                    guestsToDisplay,
                    tablesToDisplay
                };
            });
        }
    }

    componentWillMount() {

        const myGuest = this.getGuest();
        const tables = this.getTables();

        this.setState({
            guestsToDisplay: Guests,
            myGuests,
            tables,
            selectectedGuest: Guests[1],
            tablesToDisplay: Tables
        });
    }

    onGuestSelected = (guest) => {
        this.setState({selectectedGuest: guest});  
    }

    onArrivalCheck = (guest) => {
        this.setState(state => {
            const myGuests = state.myGuests.map(g => {
                if(g.CODE === guest.CODE){
                    const currTime = new Date().toLocaleTimeString();
                    g.Present = currTime;
                    return g;
                }else {
                    return g;
                }
            });
            return {
                myGuests,
            };
        });
    }

    getGuest = async() => {
        const response = await axios('api/guest_list');

        if(response.status !== 200) throw Error(response.message);
        console.log(response.data);
        return response;
    }
    getTables = async() => {
        const response = await axios('api/tables');

        if(response.status !== 200) throw Error(response.message);
        console.log(response.data);
        return response;
    }

    render() {
        return (
            <div>
                <div>
                    <SearchBar onWordEntered={this.onWordEntered} />
                </div>
                <div className="ui segment">
                    <TableList 
                        guests={this.state.guestsToDisplay} 
                        tables={this.state.tablesToDisplay} 
                        onGuestSelected={this.onGuestSelected}
                    />
                </div>
                <div className="ui segment">
                    <GuestView 
                        guest={this.state.selectectedGuest} 
                        onArrivalCheck={this.onArrivalCheck} 
                    />
                </div>
            </div>
        );
    }
}

export default App;
