import React from 'react';
import './app.css';
import SearchBar from './SearchBar';
import TableList from './TableList';
import GuestView from './GuestView';
import Guests from '../data/guest_list.json'
import Tables from '../data/tables.json'

class App extends React.Component {

    state = {myGuests: [], selectectedGuest: null};

    onWordEntered = (word) => {
        console.log(word);
    }

    componentWillMount() {
        this.setState({
            myGuests: Guests,
            selectectedGuest: Guests[1]
        });
    }

    onGuestSelected = (guest) => {
        this.setState({selectectedGuest: guest});  
    }

    onArrivalCheck = (guest) => {
        /* for(let g of this.state.myGuests){
            if(g.CODE === guest.CODE){
                g.Present = "1";
            }
        }*/

        this.state.myGuests.find(g => g.CODE === guest.code).Present = "1";
    }

    render() {
        return (
            <div>
                <div>
                    <SearchBar onWordEntered={this.onWordEntered} />
                </div>
                <div className="ui segment">
                    <TableList guests={this.state.myGuests} tables={Tables} onGuestSelected={this.onGuestSelected}/>
                </div>
                <div className="ui segment">
                    <GuestView guest={this.state.selectectedGuest} onArrivalCheck={this.onArrivalCheck} />
                </div>
            </div>
        );
    }
}

export default App;
